import{_ as e,c as t,o,ae as i}from"./chunks/framework.Cd-3tpCq.js";const m=JSON.parse('{"title":"非谓语动词","description":"","frontmatter":{},"headers":[],"relativePath":"catti/grammer/12不定式.md","filePath":"catti/grammer/12不定式.md"}'),r={name:"catti/grammer/12不定式.md"};function l(h,a,n,s,d,u){return o(),t("div",null,a[0]||(a[0]=[i('<h1 id="非谓语动词" tabindex="-1">非谓语动词 <a class="header-anchor" href="#非谓语动词" aria-label="Permalink to &quot;非谓语动词&quot;">​</a></h1><h2 id="不定式-to-do" tabindex="-1">不定式(to do) <a class="header-anchor" href="#不定式-to-do" aria-label="Permalink to &quot;不定式(to do)&quot;">​</a></h2><h3 id="句中充当成分" tabindex="-1">句中充当成分 <a class="header-anchor" href="#句中充当成分" aria-label="Permalink to &quot;句中充当成分&quot;">​</a></h3><ul><li>不定式， “不一定是什么词性的成分”，可充当除谓语之外的其他任何成分</li></ul><h4 id="主语" tabindex="-1">主语 <a class="header-anchor" href="#主语" aria-label="Permalink to &quot;主语&quot;">​</a></h4><h5 id="句首" tabindex="-1">句首 <a class="header-anchor" href="#句首" aria-label="Permalink to &quot;句首&quot;">​</a></h5><ul><li>句首的场景不到10%，只有在一些比较特殊的情况下：有两个不定式构成对比关系</li><li>To err is human, to forgive, (is) divine. // 犯错人皆难免，宽恕则属超凡</li></ul><h5 id="句尾-it-to-do" tabindex="-1">句尾(It+to do) <a class="header-anchor" href="#句尾-it-to-do" aria-label="Permalink to &quot;句尾(It+to do)&quot;">​</a></h5><ul><li>更常见的是使用<strong>it做形式主语</strong>，it<strong>不能用this或that来替换</strong>，真正的主语不定式放在句尾，通过介词for明确不定式逻辑主语，常见结构不会局限在It is +形容词+to do结构。 <ul><li>It is <strong>impossible</strong> for us to master English in a short time.</li><li>It <strong>takes time and effort</strong> to master English in a short time.</li><li>It is <strong>my duty</strong> to help you with your English.</li></ul></li><li>做主语的不定式涉及到英汉思维表达差异的问题。很多句子若用汉语说是“人”做主语；但若英语表达则<strong>是“事”作主语</strong><ul><li>同样是表达“我们不可能在很短的时间内就掌握英语”</li><li>英式思维表达：It is impossible for us to master English in a short time. √</li><li>中式思维表达：We are impossible for us to master English in a short time. × 原因是possible不能修饰人</li></ul></li></ul><h4 id="宾语" tabindex="-1">宾语 <a class="header-anchor" href="#宾语" aria-label="Permalink to &quot;宾语&quot;">​</a></h4><h4 id="宾补" tabindex="-1">宾补 <a class="header-anchor" href="#宾补" aria-label="Permalink to &quot;宾补&quot;">​</a></h4><h3 id="逻辑主语" tabindex="-1">逻辑主语 <a class="header-anchor" href="#逻辑主语" aria-label="Permalink to &quot;逻辑主语&quot;">​</a></h3><h3 id="时态、语态变化" tabindex="-1">时态、语态变化 <a class="header-anchor" href="#时态、语态变化" aria-label="Permalink to &quot;时态、语态变化&quot;">​</a></h3><h2 id="动名词-doing" tabindex="-1">动名词(doing) <a class="header-anchor" href="#动名词-doing" aria-label="Permalink to &quot;动名词(doing)&quot;">​</a></h2><h3 id="句中充当成分-1" tabindex="-1">句中充当成分 <a class="header-anchor" href="#句中充当成分-1" aria-label="Permalink to &quot;句中充当成分&quot;">​</a></h3><h3 id="逻辑主语-1" tabindex="-1">逻辑主语 <a class="header-anchor" href="#逻辑主语-1" aria-label="Permalink to &quot;逻辑主语&quot;">​</a></h3><h3 id="时态、语态变化-1" tabindex="-1">时态、语态变化 <a class="header-anchor" href="#时态、语态变化-1" aria-label="Permalink to &quot;时态、语态变化&quot;">​</a></h3><h2 id="分词-doing-done" tabindex="-1">分词(doing/done) <a class="header-anchor" href="#分词-doing-done" aria-label="Permalink to &quot;分词(doing/done)&quot;">​</a></h2><h3 id="句中充当成分-2" tabindex="-1">句中充当成分 <a class="header-anchor" href="#句中充当成分-2" aria-label="Permalink to &quot;句中充当成分&quot;">​</a></h3><h3 id="逻辑主语-2" tabindex="-1">逻辑主语 <a class="header-anchor" href="#逻辑主语-2" aria-label="Permalink to &quot;逻辑主语&quot;">​</a></h3><h3 id="时态、语态变化-2" tabindex="-1">时态、语态变化 <a class="header-anchor" href="#时态、语态变化-2" aria-label="Permalink to &quot;时态、语态变化&quot;">​</a></h3>',21)]))}const b=e(r,[["render",l]]);export{m as __pageData,b as default};
